// German Article Rules Data Structure

export interface ArticleWord {
  german: string;
  english: string;
  ukrainian?: string;
  polish?: string;
  albanian?: string;
  arabic?: string;
  italian?: string;
}

export interface ArticleRule {
  id: string;
  ruleTitle: {
    en: string;
    de: string;
    uk: string;
    pl: string;
    sq: string;
    ar: string;
    it: string;
  };
  ruleDescription: {
    en: string;
    de: string;
    uk: string;
    pl: string;
    sq: string;
    ar: string;
    it: string;
  };
  examples: string[]; // Simple endings or patterns
  words: ArticleWord[];
}

export interface ArticleCategory {
  article: 'der' | 'die' | 'das';
  rules: ArticleRule[];
}

